import React, { useState, useEffect } from "react";
import axios from "axios";

const PersonInfoComponent = ({ name }) => {
  const [personInfo, setPersonInfo] = useState(null);

  useEffect(() => {
    const fetchPersonInfo = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/swapi-proxy/person-info?name=${name}`
        );
        setPersonInfo(response.data);
      } catch (error) {
        console.error("Error fetching person information:", error);
        setPersonInfo(null);
      }
    };

    fetchPersonInfo();
  }, [name]);

  if (!personInfo) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{personInfo.name}</h2>
      <p>
        <strong>Birth Year:</strong> {personInfo.birthYear}
      </p>
      <p>
        <strong>Gender:</strong> {personInfo.gender}
      </p>
      <p>
        <strong>Planet:</strong> {personInfo.planetName}
      </p>
      <p>
        <strong>Fastest Vehicle Driven:</strong>{" "}
        {personInfo.fastestVehicleDriven}
      </p>

      <h3>Films:</h3>
      <ul>
        {personInfo.films.map((film, index) => (
          <li key={index}>
            <strong>Name:</strong> {film.name}, <strong>Release Date:</strong>{" "}
            {film.releaseDate}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PersonInfoComponent;
